Data = '''
'-- fmtGet_CategoryAttrFilter.sql\n-- aLangId, aCategoryId, CondAttrAndVal_ORs, NumberOf_ORs,\n\nwith\nwt1 as (\n    -- fmtGet_fmtGet_CategoryAttrFilterProduct.sql\n-- aLangId, aCategoryId, CondAttrAndVal_ORs, NumberOf_ORs\n\nselect\n    rpa.product_id\nfrom\n    ref_product_attr rpa\njoin\n    ref_product rp on\n    (rp.id = rpa.product_id)\njoin\n    ref_tenant rt on\n    (rt.id = rp.tenant_id)\njoin\n    ref_product0_to_category rptc0 on\n    (rptc0.product_id = rp.product0_id)\nwhere\n    (rp.enabled) and\n    (rp.product0_id is not null) and\n    (rt.enabled) and\n    (rptc0.category_id = 2) and\n    (rpa.lang_id = 1) and\n    (\n        (rpa.attr_id = 3 and rpa.val in (\'15"\'))\n        --(rpa.attr_id = 1 and rpa.val in (\'8GB\', \'16GB\')) or\n        --(rpa.attr_id = 4 and rpa.val IN (\'256GB\', \'500GB\'))\n    )\ngroup by\n    rpa.product_id\nhaving\n    count(*) = 1\n),\nwt2 as (\n    select\n        rpa.attr_id,\n        rpa.val,\n        count(*)\n    from\n        ref_product_attr rpa\n    join\n        wt1 on\n        (wt1.product_id = rpa.product_id)\n    group by\n        rpa.attr_id,\n        rpa.val\n)\nselect\n    wt2.attr_id,\n    wt2.val,\n    wt2.count,\n    ra.alias,\n    ral.title\njoin\n    ref_attr_lang ral on\n    (ral.attr_id = wt2.attr_id) and (ral.lang_id = 1)\njoin\n    ref_attr ra on\n    (ra.id = wt2.attr_id)\norder by\n    title,\n    val'
'''
with open('RowValue.txt', 'w') as F:
    F.write(Data)

